function stripFences(s) {
  if (typeof s !== 'string') return s;
  const m = s.match(/```[a-zA-Z]*\n([\s\S]*?)```/);
  return m ? m[1].trim() : s.trim();
}
function safeParse(x) {
  if (typeof x === 'object') return x;
  try { return JSON.parse(x); } catch { return null; }
}
function ensureShape(data) {
  if (Array.isArray(data)) return { DELIVERABLES: data };
  if (data && typeof data === 'object' && Array.isArray(data.DELIVERABLES)) return data;
  if (data && typeof data.output === 'string') {
    const inner = safeParse(stripFences(data.output));
    if (Array.isArray(inner)) return { DELIVERABLES: inner };
    if (inner && Array.isArray(inner.DELIVERABLES)) return inner;
  }
  return { DELIVERABLES: [data] };
}

// Only coerce spec containers to {} when missing/wrong type.
// Do NOT inject "Not specified" into spec objects.
function coerceSpecs(d) {
  if (typeof d.ImageSpec === 'string' || !d.ImageSpec || typeof d.ImageSpec !== 'object') d.ImageSpec = {};
  if (typeof d.VideoSpec === 'string' || !d.VideoSpec || typeof d.VideoSpec !== 'object') d.VideoSpec = {};
  return d;
}

function clean(obj) {
  const DROP = new Set(['Not specified', 'Not applicable', '', ' ']);
  if (Array.isArray(obj)) {
    return obj.map(clean).filter(v => {
      if (typeof v === 'string') return !DROP.has(v);
      if (Array.isArray(v)) return v.length > 0;
      if (v && typeof v === 'object') return Object.keys(v).length > 0;
      return v !== undefined && v !== null;
    });
  } else if (obj && typeof obj === 'object') {
    const out = {};
    for (const [k, v] of Object.entries(obj)) {
      const cv = clean(v);
      const dropStr = (typeof cv === 'string') && DROP.has(cv);
      const emptyArr = Array.isArray(cv) && cv.length === 0;
      const emptyObj = cv && typeof cv === 'object' && !Array.isArray(cv) && Object.keys(cv).length === 0;
      // keep empty {} ONLY for ImageSpec/VideoSpec so downstream knows the fields exist
      const isSpecKey = (k === 'ImageSpec' || k === 'VideoSpec');
      if (!dropStr && !emptyArr && (!emptyObj || isSpecKey)) out[k] = cv;
    }
    return out;
  }
  return obj;
}

const out = [];
for (const item of items) {
  let raw = item.json;
  if (typeof raw === 'string') raw = stripFences(raw);
  if (typeof raw === 'string') raw = safeParse(raw) || { output: raw };

  let normalized = ensureShape(raw);

  // coerce spec containers for every deliverable
  if (Array.isArray(normalized.DELIVERABLES)) {
    normalized.DELIVERABLES = normalized.DELIVERABLES.map(coerceSpecs);
  }

  // prune "Not specified" strings etc., but keep empty {} for ImageSpec/VideoSpec
  normalized = clean(normalized);

  out.push({ json: normalized });
}
return out;

