  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://example.com/video-editing-output.schema.json",
  "title": "Edited Video Output",
  "type": "object",
  "patternProperties": {
    "^EditedVideo\\d{2,}$": {
      "type": "array",
      "items": {
        "type": "object",
        "required": ["ID", "FileName", "FilePath", "Trim"],
        "properties": {
          "ID": {
            "type": "integer",
            "minimum": 1
          },
          "FileName": {
            "type": "string",
            "minLength": 1
          },
          "FilePath": {
            "type": "string",
            "minLength": 1
          },
          "Trim": {
            "anyOf": [
              {
                "type": "object",
                "required": ["Start", "End"],
                "properties": {
                  "Start": {
                    "type": "string",
                    "pattern": "^\\d{2}:\\d{2}:\\d{2}$",
                    "description": "Start time in HH:MM:SS format"
                  },
                  "End": {
                    "type": "string",
                    "pattern": "^\\d{2}:\\d{2}:\\d{2}$",
                    "description": "End time in HH:MM:SS format"
                  }
                },
                "additionalProperties": false
              },
              {
                "type": "null"
              }
            ]
          }
        },
        "additionalProperties": false
      },
      "minItems": 1
    }
  },
  "additionalProperties": false
}

